import React from 'react';
import './TopBanner.css';
import DailySelect from '../../components/DailySelect/DailySelect.js';


const topbanner = () =>{
  let todayChosen = false;

  toggleDayHandler = () => {
    return todayChosen = !todayChosen; 
  }
  return (
    <header className="topBanner--container">
    <div className="topBanner--header">
      Today
    </div>
    <div className="topBanner--content">
      <DailySelect />
      <DailySelect />
      <DailySelect />
      <DailySelect />
    </div>
    <i className="fas fa-angle-right fa-3x topBanner--arrow"></i>
    </header>

  );

}

export default topbanner;
