import React from 'react';
import './TopBanner.css';
import DailySelect from '../../components/DailySelect/DailySelect.js';


const topbanner = (props) => {

  const showBackBtn = ( props.showToday ? null :<button className="topBanner--arrow topBanner--arrow-left" onClick={props.changeDay}>
        <i className="fas fa-angle-left fa-3x"></i>
      </button>
  );

  const showForwardBtn = ( !props.showToday ? null :<button className="topBanner--arrow topBanner--arrow-right" onClick={props.changeDay}>
        <i className="fas fa-angle-right fa-3x"></i>
      </button>
  );

  const arrowKeysPressedHandler = (event) => {
    console.log(event);
  }

  return (
    <header className={props.showToday ? 'topBanner--container topBanner_blueBg' : 'topBanner--container topBanner_redBg'}>
    <div className="topBanner--header">
     {props.showToday ? "Today" : "Tomorrow"}
    </div>
    {showBackBtn}
    <div className="topBanner--content">
      <DailySelect onClick={() => arrowKeysPressedHandler}/>
      <DailySelect />
      <DailySelect />
      <DailySelect />
    </div>
    {showForwardBtn}
    </header>

  );

}

export default topbanner;
