import React, { Component } from 'react';
import './App.css';
import Navbar from '../components/Navbar/Navbar.js';
import DailySelectContainer from './DailySelectContainer/DailySelectContainer.js';
import ResultContainer from './ResultContainer/ResultContainer';


class App extends Component {
  state = {
    menuOptionChosen: 'PVE',
    filter: {
      Type: 'ALL',
      Area: 'ALL'
    },
    showToday: true,
 };

 changeDayHandler = () => {
   this.setState({
     showToday: !this.state.showToday
   });
 }
 changeMenuOptionChoosenHandler = (item) => {
    this.setState({
        menuOptionChosen: item
    });
  };

  setTopSection = () => {
    if(this.state.menuOptionChosen === "SEARCH")
      return  <DailySelectContainer showToday={this.state.showToday} changeDay={this.changeDayHandler}/>;
  };

  render() {
    return (
      <div className="App">
        <div className="navbar--container">
          <Navbar optionChosen={this.state.menuOptionChosen} clicked={this.changeMenuOptionChoosenHandler}/>
        </div>
        <div className="main-content">
          {this.setTopSection}
          <DailySelectContainer showToday={this.state.showToday} changeDay={this.changeDayHandler}/>
          <ResultContainer filter={this.state.filter} menuOption={this.state.menuOptionChosen}/>
        </div>
      </div>
    );
  }
}

export default App;
