import React from 'react';
import './SearchTopSection.css';
import Dropdown from 'mineral-ui/Dropdown';
import Button from 'mineral-ui/Button';
import { ThemeProvider } from 'mineral-ui/themes';
import ForagerIcon from '../../assets/images/icons/harvestIcon.png';


const searchTopSection = (props) => {

  const typeOptionsList = [
    {text: "All", value: "ALL" },
    {text: "Miner", value: "Miner" },
    {text: "Forager", value: "Forager"},
    {text: "Lumberer", value: "Lumberer"},
    {text: "Vista Viewer", value: "Vista Viewer"},
    {text: "Minidungeon", value: "Minidungeon"}
  ];

  const dailySelect = typeOptionsList.map((daily, index) => {
      return (
        <option value={daily.value} key={index}>{daily.name}</option>
      );
    });

  return (
    <ThemeProvider>
      <header className='SearchTop--container topBanner_blueBg'>
        <h1 className="SearchTop--header">
         Search for locations
        </h1>
        <div className="SearchTop--content">
        <label>
          Choose daily:
          <Dropdown data={typeOptionsList}>
            <Button iconEnd={ForagerIcon} className='dropdownStyling'>Menu</Button>
          </Dropdown>
        </label>
        <select>
          <option>All</option>
        </select>
        </div>

      </header>
    </ThemeProvider>
  );

}


export default searchTopSection;
